import React, { Component } from 'react';
class TabelaNotas extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        return (
            <div className="row">
                <div className="col">
                    <table class="table caption-top">
                        <caption>Lista de Notas Cadastradas</caption>
                        <thead>
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Titulo</th>
                                <th scope="col">Texto da Nota</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Array.of("Julio", "Ingrid", "Otavio", "Lucas").map((titulo, index) => {
                                return (
                                    <>
                                        <tr>
                                            <th scope="row" key={index}>1</th>
                                            <td>{titulo}</td>
                                            <td>Mensagem</td>
                                        </tr>
                                    </>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default TabelaNotas;
