import React, { Component } from 'react';
import TabelaNotas from '../TabelaNotas/';

class Formulario extends Component {
  constructor(){
    super();
    this._titulo = "";
    this._texto = "";
  }

  _handleTitulo(evento){
    evento.stopPropagation();
    this._texto = evento.target.titulo;
    //console.log(evento.target.value);
  }

  _handleTexto(evento){
    evento.stopPropagation();
    this._texto = evento.target.value;
    console.log(this._texto);

  }

  render() {
    return (
      <section>
        <div className="row">
          <div className="col">
            <form>
              <div className="row mb-3">
                <label for="inputEmail3" className="col-sm-2 col-form-label">Titulo</label>
                <div className="col-sm-10">
                  <input type="text" className="form-control" id="inputEmail3" onChange={this._handleTitulo.bind(this)}/>
                </div>
              </div>
              <div className="row mb-3">
                <label for="inputPassword3" className="col-sm-2 col-form-label">Nota em Texto</label>
                <div className="col-sm-10">
                  <textarea rows={8} className="form-control" id="inputPassword3" onChange={this._handleTexto.bind(this)}/>
                </div>
              </div>
              <button type="submit" className="btn btn-primary">Cadastrar Nota</button>
            </form>
          </div>
        </div>
      </section>
    );
  }
}

export default Formulario;
